import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import AccountBalance from './AccountBalance';

class Debits extends Component{
    constructor(props){
        super(props)
        this.state ={
            debits: this.props.allDebits,
            description: [],
            amount: [],
            date: [],
        }
    }

    handleSubmit = (event) => {
        // const inputValue = description.target.value;
        // console.log(event);
    }

    handleChangeDescription = (des) => {
        // console.log(des.target.value);
        let tempDescription = this.state.description;
        tempDescription.push(des);
        this.setState({description: tempDescription });
        console.log(this.state.description);
    }

    render(){
        return(
            <div>
                <h1>Debits</h1>
                {
                    this.props.allDebits.map( (debit) => {
                        return (
                            <div>
                                <li>{debit.amount} - {debit.description} - {debit.date}</li>
                            </div>
                        )
                    })
                }
                <div>
                <br></br>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Description:
                        <input type="text" name="description" onChange={this.handleChangeDescription}/>
                        Amount:
                        <input type="number" name="amount" />
                        Date:
                        <input type="date" name="date" />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                </div>
                <br></br>
                <Link to="/">Home</Link>
                <div></div>
                <Link to="/logIn">Log In</Link>
                <div></div>
                <Link to="/userProfile">User Profile</Link>
                <div></div>
            </div>
        );
    }
}

export default Debits;
