import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import AccountBalance from './AccountBalance';

class Debits extends Component{
    constructor(props){
        super(props)
        this.state = {
            alldebits: [],
            description: '',
            amount: 0,
            date: '',
            alldescription: [],
            allamount: [],
            alldate: [],
        }
    }
    componentDidMount() {
        let descipt = [], money = [], day = [];
        for (let i=0; i< this.props.allDebits.length; i++)  {
            descipt.push(this.props.allDebits[i].description);
            money.push(this.props.allDebits[i].amount);
            day.push(this.props.allDebits[i].date);
        }
        this.setState({
            alldescription: descipt,
            allamount: money,
            alldate: day,
        })
    }

    // submitDebit = () => {
    //     this.setState({alldebits: this.state.alldebits.push(this.state.amount)});
    //     console.log(this.state.alldebits);
    // }

    handleChangeDescription = (des) => {
        this.setState({description: des.target.value });
    }
    handleChangeAmount = (num) => {
        this.setState({amount: num.target.value});
        // console.log(this.state.amount);
    }
    handleChangeDate = (day) => {
        this.setState({date: day.target.value});
        // console.log(this.state.date);
    }

    render(){
        return(
            <div>
                <h1>Debits</h1>
                {
                    this.props.allDebits.map( (debit) => {
                        return (
                            <div>
                                <li>{debit.amount} - {debit.description} - {debit.date}</li>
                            </div>
                        )
                    })
                }

                <div>
                <br></br>
                <form>
                    <label>
                        Description:
                        <input type="text" name="description" onChange={this.handleChangeDescription}/>
                        Amount:
                        <input type="number" name="amount" onChange={this.handleChangeAmount}/>
                        Date:
                        <input type="date" name="date" onChange={this.handleChacngeDate}/>
                    </label>
                    <button>Submit</button>
                </form>
                </div>
                <br></br>
                <Link to="/">Home</Link>
                <div></div>
                <Link to="/logIn">Log In</Link>
                <div></div>
                <Link to="/userProfile">User Profile</Link>
                <div></div>
            </div>
        );
    }
}

export default Debits;
